package leetcode.LeetCode;

import static org.junit.Assert.*;

import org.junit.Test;

public class MinimumWindowSubstringTest {
	
	String i1 = "ADOBECODEBANC";
	String i2 = "ABC";
	
	String i3 = "a";
	String i4 = "a";
	
	String i5 = "";
	String i6 = "";
	
	String i7 = "a";
	String i8 = "aa";
	
	String i9 = "ab";
	String i10 = "aa";
	
	String i11 = "bba";
	String i12 = "ab";
	
	String i13 = "bbaa";
	String i14 = "aba";
	
	String i15 = "bbaac";
	String i16 = "aba";
	
	String i17 = "";
	String i18 = "";
	
	
	MinimumWindowSubstring m = new MinimumWindowSubstring();

	@Test
	public void test() {
		assertEquals("baa",m.minWindow(i15, i16));
		assertEquals("baa",m.minWindow(i13, i14));
		assertEquals("BANC",m.minWindow(i1, i2));
		assertEquals("a",m.minWindow(i3, i4));
		assertEquals("",m.minWindow(i5, i6));
		assertEquals("",m.minWindow(i7, i8));
		assertEquals("",m.minWindow(i9, i10));
		assertEquals("ba",m.minWindow(i11, i12));
		//assertEquals("baa",m.minWindow(i17, i18));
	}

}
